import React from 'react';
import { View, ActivityIndicator, Image, StyleSheet, Animated } from 'react-native';
import { useNavigation } from '@react-navigation/native';

const LoadingScreen = () => {
    const [isLoading, setIsLoading] = useState(true);
    const opacity = new Animated.Value(1); // Initialize opacity value
    const navigation = useNavigation();
  
    // Simulate the splash screen duration
    useEffect(() => {
      const fadeOut = () => {
        Animated.timing(opacity, {
          toValue: 0,
          duration: 1000, // Adjust the duration as needed
          useNativeDriver: false, // Set to false for Android support
        }).start(() => {
          setIsLoading(false); // Set isLoading to false after animation
          navigation.navigate('Main'); // Replace 'Main' with your main screen name
        });
      };
  
      setTimeout(fadeOut, 3000); // Adjust the duration (in milliseconds) as needed
    }, []);
  
    return (
      <View style={styles.container}>
        <Animated.Image
          source={require('../images/logo-white.png')}
          style={[styles.image, { opacity }]}
        />
        {isLoading && <ActivityIndicator size="large" color="white" />}
      </View>
    );
  };
  
  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: 'black',
      justifyContent: 'center',
      alignItems: 'center',
    },
    image: {
      width: 500, // Adjust the width and height as needed
      height: 200,
      resizeMode: 'contain', // Adjust the resizeMode as needed
    },
  });
  
  export default LoadingScreen;
