// App.js

import React, { useState, useEffect } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import LoadingScreen from './components/pages/LoadingScreen'; // Import your LoadingScreen component
import LoadingTransition from './components/pages/LoadingTransition'; // Import your LoadingTransition component
import GetStarted from './components/pages/GetStarted'; // Import your GetStarted component

const AppStack = createStackNavigator();

const App = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [isTitleLoading, setIsTitleLoading] = useState(true);

  useEffect(() => {
    // Simulate initial loading with a setTimeout
    setTimeout(() => {
      setIsLoading(false);
    }, 2000);

    // After the initial loading is done, show the title loading screen for a while
    setTimeout(() => {
      setIsTitleLoading(false);
    }, 2000); // Replace with your desired time
  }, []);

  return (
    <NavigationContainer>
      <AppStack.Navigator>
        {isLoading ? (
          // Display the initial loading screen
          <AppStack.Screen
            name="LoadingScreen"
            component={LoadingScreen}
            options={{ headerShown: false }}
          />
        ) : isTitleLoading ? (
          // Display the second loading screen
          <AppStack.Screen
            name="LoadingTransition"
            component={LoadingTransition}
            options={{ headerShown: false }}
          />
        ) : (
          // navigate to the GetStarted screen
          <AppStack.Screen
            name="GetStarted"
            component={GetStarted}
            options={{ headerShown: false }}
          />
        )}
      </AppStack.Navigator>
    </NavigationContainer>
  );
};

export default App;
